import React, { useState } from 'react';

const Register = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleRegister = async () => {
        // Perform validation on name, email, and password fields
        if (!name || !email || !password) {
            alert('All fields are required.');
            return;
        }

        try {
            // Make a POST request to your backend API to register the user
            const response = await fetch('/api/register', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ name, email, password }),
            });

            if (response.ok) {
                // Registration successful
                const data = await response.json();
                alert(`Registration successful for user with email: ${data.email}`);
                // Perform any additional actions upon successful registration
            } else {
                // Registration failed
                const error = await response.json();
                alert(error.message);
            }
        } catch (error) {
            console.error('An error occurred during registration:', error);
            alert('An error occurred during registration. Please try again later.');
        }
    };

    return (
        <div>
            <h2>Register</h2>
            <div>
                <label>Name:</label>
                <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
            </div>
            <div>
                <label>Email:</label>
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
            </div>
            <div>
                <label>Password:</label>
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
            </div>
            <button onClick={handleRegister}>Register</button>
        </div>
    );
};

export default Register;
