import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { resetUserSession, getToken, getUser } from './service/AuthService.js';
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Box from "@mui/material/Box";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemText from "@mui/material/ListItemText";
import MenuItem from "@mui/material/MenuItem";
import Select from "@mui/material/Select";
import FormControl from "@mui/material/FormControl";
import InputLabel from "@mui/material/InputLabel";
const backendUrl = process.env.REACT_APP_BACKEND_URL;

const PremiumContent = (props) => {
    const email = JSON.parse(sessionStorage.getItem('user'))?.email || '';
    const [books, setBooks] = useState([]);
    const [newBook, setNewBook] = useState('');
    const [selectedBook, setSelectedBook] = useState('');
    const [bookStatus, setBookStatus] = useState('');


    const fetchBookById = async (bookId) => {
        try {
          const response = await axios.get(`${backendUrl}/book?id=${bookId}`, {
            headers: {
              'Content-Type': 'application/json',
              "Access-Control-Allow-Origin": "*",
              "Access-Control-Allow-Methods": "GET, PUT, POST, DELETE, OPTIONS, PATCH"
            },
          });
          console.log(response.data); // log or do something with the book
        } catch (error) {
          console.error('Error fetching book:', error);
        }
      };


    const fetchBooks = async () => {
        try {
          const response = await axios.get(`${backendUrl}/books?userEmail=${email}`, {
            headers: {
                'Content-Type': 'application/json',
                "Access-Control-Allow-Origin": "*",
                "Access-Control-Allow-Methods": "GET, PUT, POST, DELETE, OPTIONS, PATCH"
            },
          });
          setBooks(response.data.books);
        } catch (error) {
          console.error('Error fetching books:', error);
        }
      };


    useEffect(() => {
        fetchBooks();
    }, []); // Empty dependency array to run the effect only once


    const handleAddBook = async () => {
        try {
            const response = await axios.post(
                `${backendUrl}/book`,
                {
                  name: newBook,
                  status: 'Available',
                  userEmail: email, // add userEmail
                },
                {
                  headers: {
                    'Content-Type': 'application/json',
              "Access-Control-Allow-Origin": "*",
              "Access-Control-Allow-Methods": "GET, PUT, POST, DELETE, OPTIONS, PATCH"
                  },
                }
              );

              if (response.status === 200) {
                const updatedBook = response.data.UpdatedAttributes; // get updated book attributes
                setBooks((prevBooks) =>
                    prevBooks.map((book) => (book.id === updatedBook.id ? updatedBook : book))
                );
                setSelectedBook('');
                setBookStatus('');
                fetchBooks(); // Refresh books after successful status update
            }
             else {
                // Handle error if necessary
            }
        } catch (error) {
            console.error('Error adding book:', error);
            // Handle error if necessary
        }
    };


    const handleUpdateStatus = async () => {
        if (!selectedBook || !bookStatus) {
          // make sure a book is selected and a new status is provided
          console.error('Please select a book and a new status.');
          return;
        }

        try {
            const response = await axios.patch(
                `${backendUrl}/book`,
                {
                    id: selectedBook,
                    updateKey: 'status', // always updating the 'status' field
                    updateValue: bookStatus,
                },
                {
                    headers: {
                        'Content-Type': 'application/json',
                    },
                }
            );

            if (response.status === 200) {
                console.log(response.data);  // Log the entire response data for debugging
                const updatedBook = response.data.UpdatedAttributes; // get updated book attributes
                setBooks((prevBooks) =>
                    prevBooks.map((book) => (book.id === updatedBook.id ? updatedBook : book))
                );
                setSelectedBook('');
                setBookStatus('');
                fetchBooks(); // Refresh books after successful status update
            }
        } catch (error) {
            console.error('Error updating book status:', error);
        }
    };



    const handleDeleteBook = async (bookId) => {
        try {
            await axios.delete(
                `${backendUrl}/book?id=${bookId}&userEmail=${email}`,
                {
                  headers: {
                    'Content-Type': 'application/json',
                  },
                }
              );

          // Book deletion successful
          setBooks(prevBooks => prevBooks.filter(book => book.id !== bookId));
        } catch (error) {
          console.error('Error deleting book:', error);
          // Handle error if necessary
        }
      };

    const logoutHandler = () => {
        resetUserSession();
        props.history.push('/login');
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', padding: '20px' }}>
            <div style={{ maxWidth: '600px', width: '100%' }}>
            <Typography variant="h4" gutterBottom>Hello {email}!</Typography>

                <Button variant="contained" color="primary" onClick={logoutHandler}>
                    Logout
                </Button>

                <Typography variant="h5" gutterBottom style={{ marginTop: '20px' }}>
                    Book Library
                </Typography>

                <Typography variant="h6" gutterBottom>
                    Books
                </Typography>
                <List>
                    {books.map((book) => (
                        <ListItem key={book.id}>
                            <ListItemText primary={`${book.name} - ${book.status}`} />
                            <Button color="secondary" onClick={() => handleDeleteBook(book.id)}>
                                Delete
                            </Button>
                        </ListItem>
                    ))}
                </List>

                <Typography variant="h6" gutterBottom style={{ marginTop: '20px' }}>
                    Add Book
                </Typography>
                <Box display="flex" alignItems="center" style={{ marginBottom: '20px' }}>
                    <TextField
                        label="Book Name"
                        value={newBook}
                        onChange={(e) => setNewBook(e.target.value)}
                        variant="outlined"
                    />
                    <Button variant="contained" color="primary" onClick={handleAddBook} style={{ marginLeft: '10px' }}>
                        Add Book
                    </Button>
                </Box>

                <Typography variant="h6" gutterBottom>
                    Update Book Status
                </Typography>
                <FormControl variant="outlined" style={{ minWidth: '200px', marginBottom: '20px' }}>
                    <InputLabel>Select a book</InputLabel>
                    <Select
                        value={selectedBook}
                        onChange={(e) => setSelectedBook(e.target.value)}
                        label="Select a book"
                    >
                        <MenuItem value="">Select a book</MenuItem>
                        {books.map((book) => (
                            <MenuItem key={book.id} value={book.id}>
                                {book.name}
                            </MenuItem>
                        ))}
                    </Select>
                </FormControl>
                <Box display="flex" alignItems="center">
                    <FormControl variant="outlined" style={{ marginRight: '10px', minWidth: '200px' }}>
                        <InputLabel>Select new status</InputLabel>
                        <Select
                            value={bookStatus}
                            onChange={(e) => setBookStatus(e.target.value)}
                            label="Select new status"
                        >
                            <MenuItem value="In-Progress">In-Progress</MenuItem>
                            <MenuItem value="Not Started">Not Started</MenuItem>
                            <MenuItem value="Finished">Finished</MenuItem>
                        </Select>
                    </FormControl>
                    <Button variant="contained" color="primary" onClick={handleUpdateStatus}>
                        Update Status
                    </Button>
                </Box>

            </div>
        </div>
    );
};

export default PremiumContent;
