import { axiosInstance, getUser, getToken, resetUserSession } from '/Users/mayankpandey/Desktop/mpandey/TermAssignment/BookAvengers-Frontend/src/service/AuthService.js';

const PremiumContent = (props) => {
    const user = getUser();
    const name = user !== 'undefined' && user ? user.name : '';

    const logoutHandler = () => {
        resetUserSession();
        props.history.push('/login');
    };

    return (
        <div>
            Hello {name}!
            <input type="button" value="Logout" onClick={logoutHandler} />
            <Books email={user.email} />
        </div>
    );
};

const Books = ({ email }) => {
    const [books, setBooks] = useState([]);
    const [newBook, setNewBook] = useState({
        title: '',
        author: '',
        email: email || '',
    });

    const fetchBooks = useCallback(async () => {
        try {
            const response = await axiosInstance.post('/get-books', { email: newBook.email });
            if (response.status === 200) {
                setBooks(response.data.books);
            } else {
                console.error('Failed to fetch books:', response.data.message);
            }
        } catch (error) {
            console.error('Failed to fetch books:', error);
        }
    }, [newBook.email]);

    useEffect(() => {
        fetchBooks();
    }, [newBook.email, fetchBooks]);

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setNewBook((prevBook) => ({
            ...prevBook,
            [name]: value,
        }));
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await axiosInstance.post('/add-book', {
                book: newBook,
                user: { email: newBook.email, token: getToken() },
            });
            if (response.status === 200) {
                setNewBook({
                    title: '',
                    author: '',
                    email: newBook.email,
                });
                fetchBooks(); // Refresh books list after adding a new book
            } else {
                console.error('Failed to add book:', response.data.message);
            }
        } catch (error) {
            console.error('Failed to add book:', error);
        }
    };

    const handleDeleteBook = async (bookId) => {
        try {
            const response = await axiosInstance.post('/delete-book', { bookId });
            if (response.status === 200) {
                fetchBooks(); // Refresh books list after deleting a book
            } else {
                console.error('Failed to delete book:', response.data.message);
            }
        } catch (error) {
            console.error('Failed to delete book:', error);
        }
    };

    const handleUpdateBookStatus = async (bookId, status) => {
        try {
            const response = await axiosInstance.post('/update-book-status', { bookId, status });
            if (response.status === 200) {
                await fetchBooks(); // Refresh books list after updating the book status
            } else {
                console.error('Failed to update book status:', response.data.message);
            }
        } catch (error) {
            console.error('Failed to update book status:', error);
        }
    };

    return (
        <div>
            <h2>Books</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="title"
                    placeholder="Title"
                    value={newBook.title}
                    onChange={handleInputChange}
                />
                <input
                    type="text"
                    name="author"
                    placeholder="Author"
                    value={newBook.author}
                    onChange={handleInputChange}
                />
                <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={newBook.email}
                    onChange={handleInputChange}
                />
                <button type="submit">Add Book</button>
            </form>

            <h3>Book List</h3>
            <ul>
                {books.map((book) => (
                    <li key={book.id}>
                        <strong>Title:</strong> {book.title}
                        <br />
                        <strong>Author:</strong> {book.author}
                        <br />
                        <strong>Status:</strong> {book.status}
                        <br />
                        <button onClick={() => handleDeleteBook(book.id)}>Delete</button>
                        <button onClick={() => handleUpdateBookStatus(book.id, 'Read')}>Mark as Read</button>
                        <button onClick={() => handleUpdateBookStatus(book.id, 'Unread')}>
                            Mark as Unread
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default PremiumContent;
