import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { getUser, resetUserSession } from './service/AuthService';
import { makeStyles } from '@material-ui/core/styles';
import {
    Typography,
    Button,
    TextField,
    Select,
    MenuItem,
    FormControl,
    InputLabel,
    List,
    ListItem,
    ListItemText,
    ListItemSecondaryAction,
    IconButton,
} from '@material-ui/core';
import { Delete as DeleteIcon } from '@material-ui/icons';

const useStyles = makeStyles((theme) => ({
    root: {
        margin: theme.spacing(3),
    },
    header: {
        marginBottom: theme.spacing(2),
    },
    logoutButton: {
        marginBottom: theme.spacing(2),
    },
    form: {
        display: 'flex',
        marginBottom: theme.spacing(2),
    },
    formControl: {
        marginRight: theme.spacing(2),
        minWidth: 200,
    },
    button: {
        marginLeft: theme.spacing(2),
    },
    booksList: {
        marginBottom: theme.spacing(2),
    },
}));

const PremiumContent = (props) => {
    const classes = useStyles();

    const [books, setBooks] = useState([]);
    const [newBook, setNewBook] = useState('');
    const [selectedBook, setSelectedBook] = useState('');
    const [bookStatus, setBookStatus] = useState('');
    const user = getUser();
    const email = user !== 'undefined' && user ? user.email : '';

    const fetchBooks = async () => {
        try {
            const response = await axios.get('https://0i2oilda27.execute-api.us-east-1.amazonaws.com/prod/books', {
                headers: {
                    Authorization: `Bearer ${user.token}`,
                },
            });
            setBooks(response.data.books);
        } catch (error) {
            console.error('Error fetching books:', error);
        }
    };

    useEffect(() => {
        fetchBooks();
    }, []); // Empty dependency array to run the effect only once

    const handleAddBook = async () => {
        try {
            const response = await axios.post(
                'https://0i2oilda27.execute-api.us-east-1.amazonaws.com/prod/book',
                {
                    name: newBook,
                    status: 'Available',
                },
                {
                    headers: {
                        Authorization: `Bearer ${user.token}`,
                    },
                }
            );
            setNewBook('');
            setBooks((prevBooks) => [...prevBooks, response.data.Item]);
            fetchBooks(); // Refresh books after successful addition
        } catch (error) {
            console.error('Error adding book:', error);
        }
    };

    const handleUpdateStatus = async () => {
        try {
            const response = await axios.patch(
                `https://0i2oilda27.execute-api.us-east-1.amazonaws.com/prod/book`,
                {
                    id: selectedBook,
                    updateKey: 'status',
                    updateValue: bookStatus,
                },
                {
                    headers: {
                        Authorization: `Bearer ${user.token}`,
                    },
                }
            );
            const updatedBook = response.data.UpdatedAttributes;
            setBooks((prevBooks) =>
                prevBooks.map((book) => (book.id === updatedBook.id ? updatedBook : book))
            );
            setSelectedBook('');
            setBookStatus('');
            fetchBooks(); // Refresh books after successful status update
        } catch (error) {
            console.error('Error updating book status:', error);
        }
    };

    const handleDeleteBook = async (bookId) => {
        try {
            await axios.delete(
                'https://0i2oilda27.execute-api.us-east-1.amazonaws.com/prod/book',
                {
                    data: { id: bookId },
                    headers: {
                        Authorization: `Bearer ${user.token}`,
                    },
                }
            );
            setBooks((prevBooks) => prevBooks.filter((book) => book.id !== bookId));
        } catch (error) {
            console.error('Error deleting book:', error);
        }
    };

    const logoutHandler = () => {
        resetUserSession();
        props.history.push('/login');
    };

    return (
        <div className={classes.root}>
            <Typography variant="h5" className={classes.header}>
                Hello {email}!
            </Typography>
            <Button
                variant="contained"
                color="secondary"
                className={classes.logoutButton}
                onClick={logoutHandler}
            >
                Logout
            </Button>

            <Typography variant="h4">Book Library</Typography>

            <Typography variant="h5">Books</Typography>
            <List className={classes.booksList}>
                {books.map((book) => (
                    <ListItem key={book.id}>
                        <ListItemText primary={`${book.name} - ${book.status}`} />
                        <ListItemSecondaryAction>
                            <IconButton edge="end" onClick={() => handleDeleteBook(book.id)}>
                                <DeleteIcon />
                            </IconButton>
                        </ListItemSecondaryAction>
                    </ListItem>
                ))}
            </List>

            <Typography variant="h5">Add Book</Typography>
            <form className={classes.form} onSubmit={handleAddBook}>
                <TextField
                    label="Book Name"
                    value={newBook}
                    onChange={(e) => setNewBook(e.target.value)}
                />
                <Button type="submit" variant="contained" color="primary" className={classes.button}>
                    Add Book
                </Button>
            </form>

            <div>
                <Typography variant="h5">Update Book Status</Typography>
                <FormControl className={classes.formControl}>
                    <InputLabel>Select a book</InputLabel>
                    <Select value={selectedBook} onChange={(e) => setSelectedBook(e.target.value)}>
                        <MenuItem value="">
                            <em>None</em>
                        </MenuItem>
                        {books.map((book) => (
                            <MenuItem key={book.id} value={book.id}>
                                {book.name}
                            </MenuItem>
                        ))}
                    </Select>
                </FormControl>
                <TextField
                    label="New Status"
                    value={bookStatus}
                    onChange={(e) => setBookStatus(e.target.value)}
                />
                <Button
                    variant="contained"
                    color="primary"
                    className={classes.button}
                    onClick={handleUpdateStatus}
                >
                    Update Status
                </Button>
            </div>
        </div>
    );
};

export default PremiumContent;
