import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { getUser, resetUserSession } from './service/AuthService';

const PremiumContent = (props) => {
    const [books, setBooks] = useState([]);
    const [newBook, setNewBook] = useState('');
    const [selectedBook, setSelectedBook] = useState('');
    const [bookStatus, setBookStatus] = useState('');
    const user = getUser();
    const name = user !== 'undefined' && user ? user.name : '';

    useEffect(() => {
        fetchBooks();
    }, []);

    const fetchBooks = async () => {
        try {
            const response = await axios.get('https://0i2oilda27.execute-api.us-east-1.amazonaws.com/prod/books');
            setBooks(response.data.books);
        } catch (error) {
            console.error('Error fetching books:', error);
        }
    };

    const handleAddBook = async () => {
        try {
            const response = await axios.post('https://0i2oilda27.execute-api.us-east-1.amazonaws.com/prod/book', {
                name: newBook,
                status: 'Available',
            });
            setNewBook('');
            setBooks((prevBooks) => [...prevBooks, response.data.Item]);
        } catch (error) {
            console.error('Error adding book:', error);
        }
    };

    const handleUpdateStatus = async () => {
        try {
            const response = await axios.patch(`https://0i2oilda27.execute-api.us-east-1.amazonaws.com/prod/book?id=${selectedBook}`, {
                updateKey: 'status',
                updateValue: bookStatus,
            });
            const updatedBook = response.data.UpdatedAttributes;
            setBooks((prevBooks) =>
                prevBooks.map((book) => (book.id === updatedBook.id ? updatedBook : book))
            );
            setSelectedBook('');
            setBookStatus('');
        } catch (error) {
            console.error('Error updating book status:', error);
        }
    };

    const handleDeleteBook = async (bookId) => {
        try {
            await axios.delete('https://0i2oilda27.execute-api.us-east-1.amazonaws.com/prod/book', {
                data: { id: bookId },
            });
            setBooks((prevBooks) => prevBooks.filter((book) => book.id !== bookId));
        } catch (error) {
            console.error('Error deleting book:', error);
        }
    };

    const logoutHandler = () => {
        resetUserSession();
        props.history.push('https://0i2oilda27.execute-api.us-east-1.amazonaws.com/prod/login');
    };

    return (
        <div>
            Hello {name}!
            <input type="button" value="logout" onClick={logoutHandler} />

            <h1>Book Library</h1>

            <h2>Books</h2>
            <ul>
                {books.map((book) => (
                    <li key={book.id}>
                        {book.name} - {book.status}
                        <button onClick={() => handleDeleteBook(book.id)}>Delete</button>
                    </li>
                ))}
            </ul>

            <h2>Add Book</h2>
            <input
                type="text"
                value={newBook}
                onChange={(e) => setNewBook(e.target.value)}
            />
            <button onClick={handleAddBook}>Add Book</button>

            <h2>Update Book Status</h2>
            <select
                value={selectedBook}
                onChange={(e) => setSelectedBook(e.target.value)}
            >
                <option value="">Select a book</option>
                {books.map((book) => (
                    <option key={book.id} value={book.id}>
                        {book.name}
                    </option>
                ))}
            </select>
            <input
                type="text"
                value={bookStatus}
                onChange={(e) => setBookStatus(e.target.value)}
            />
            <button onClick={handleUpdateStatus}>Update Status</button>
        </div>
    );
};

export default PremiumContent;
